mapper.select:
select

    distinct


from hzh_general




    order by ${orderByClause}



        limit ${offset}, ${limit}


        limit ${limit}

select * from hzh_general where 1 = 1

    and `name` like concat('%','${hzhGeneral.name}','%')


    and birthday = #{birthday,jdbcType=VARCHAR}


    and age = #{hzhGeneral.age,jdbcType=INTEGER}


    and province = #{province,jdbcType=VARCHAR}


    and city = #{city,jdbcType=VARCHAR}


    and town = #{town,jdbcType=VARCHAR}


    and nation = #{nation,jdbcType=VARCHAR}


    and sfname = #{sfname,jdbcType=VARCHAR}


    and relation = #{relation,jdbcType=VARCHAR}


    and address = #{address,jdbcType=VARCHAR}


    and mobile = #{mobile,jdbcType=VARCHAR}


    and sicknum = #{sicknum,jdbcType=VARCHAR}


    and doctor = #{doctor,jdbcType=VARCHAR}


    and survey_time = #{surveyTime,jdbcType=VARCHAR}


    and livenum = #{livenum,jdbcType=VARCHAR}


    and six = #{six,jdbcType=VARCHAR}


    and citys = #{citys,jdbcType=VARCHAR}


    and xz = #{xz,jdbcType=VARCHAR}


    and nc = #{nc,jdbcType=VARCHAR}


    and ne = #{ne,jdbcType=VARCHAR}


    and mather = #{mather,jdbcType=INTEGER}


    and father = #{father,jdbcType=INTEGER}


    and tais = #{tais,jdbcType=INTEGER}


    and chan = #{chan,jdbcType=INTEGER}


    and zhou = #{zhou,jdbcType=INTEGER}


    and ctype = #{ctype,jdbcType=VARCHAR}


    and kg = #{kg,jdbcType=INTEGER}


    and myqbs = #{myqbs,jdbcType=INTEGER}


    and myqbses = #{myqbses,jdbcType=VARCHAR}


    and shbs = #{shbs,jdbcType=VARCHAR}


    and fych = #{fych,jdbcType=INTEGER}


    and `action` = #{action,jdbcType=VARCHAR}


    and actions = #{actions,jdbcType=VARCHAR}


    and tai = #{tai,jdbcType=INTEGER}


    and fan = #{fan,jdbcType=INTEGER}


    and dz = #{dz,jdbcType=INTEGER}


    and pa = #{pa,jdbcType=INTEGER}


    and zou = #{zou,jdbcType=INTEGER}


    and lau = #{lau,jdbcType=VARCHAR}


    and laus = #{laus,jdbcType=VARCHAR}
